const fs = require('fs-extra');
const pathResolver = require('./pathResolver');

const root = `${pathResolver.getProjectRootAbsolutePath()}`;
const localeDest = `${pathResolver.getProjectRootAbsolutePath()}/locale`;
const dateUtilsDest = `${pathResolver.getProjectRootAbsolutePath()}/dateUtils`;
const localeSource = `${root}/lib/locale`;
const dateUtilsSource = `${root}/lib/dateUtils`;

function copyFiles(source, dest) {
    if (!fs.pathExistsSync(dest)) fs.mkdirSync(dest);

    fs.readdir(source, (err, files) => {
        files.forEach(file => {
            fs.copyFileSync(`${source}/${file}`, `${dest}/${file}`);
        });
    });
}

copyFiles(localeSource, localeDest);
copyFiles(dateUtilsSource, dateUtilsDest);
