/* eslint-disable import/prefer-default-export */
/* eslint-disable no-unused-vars */
import { edsWarning } from '@element/shared';
import utility from './utilityColors';
import * as element from './element.theme';
import * as velocity from './velocity.theme';
import * as bayer from './bayer.theme';
import * as bayerplusrewards from './bayerplusrewards.theme';
import * as asgrow from './asgrow.theme';
import * as channel from './channel.theme';
import * as dad from './dad.theme';
import * as dekalb from './dekalb.theme';
import * as deltapine from './deltapine.theme';
import * as devtools from './devtools.theme';
import * as fontanelle from './fontanelle.theme';
import * as goldcountry from './goldcountry.theme';
import * as hcp from './hcp.theme';
import * as hubnerseed from './hubnerseed.theme';
import * as intacta from './intacta.theme';
import * as jungseedgenetics from './jungseedgenetics.theme';
import * as kruger from './kruger.theme';
import * as latijereta from './latijereta.theme';
import * as lewishybrids from './lewishybrids.theme';
import * as monsoy from './monsoy.theme';
import * as rea from './rea.theme';
import * as specialtyhybrids from './specialtyhybrids.theme';
import * as stewart from './stewart.theme';
import * as stoneseed from './stoneseed.theme';
import * as vegstate from './vegstate.theme';
import * as verquvo from './verquvo.theme';
import * as westbred from './westbred.theme';

export const getColorRamp = (colorRamp, theme) => {
    let ramp;
    switch (theme) {
        case 'bayer':
            ramp = bayer[colorRamp];
            break;
        case 'bayerplusrewards':
            ramp = bayerplusrewards[colorRamp];
            break;
        case 'velocity':
            ramp = velocity[colorRamp];
            break;
        case 'asgrow':
            ramp = asgrow[colorRamp];
            break;
        case 'channel':
            ramp = channel[colorRamp];
            break;
        case 'dad':
            ramp = dad[colorRamp];
            break;
        case 'dekalb':
            ramp = dekalb[colorRamp];
            break;
        case 'deltapine':
            ramp = deltapine[colorRamp];
            break;
        case 'devtools':
            ramp = devtools[colorRamp];
            break;
        case 'fontanelle':
            ramp = fontanelle[colorRamp];
            break;
        case 'goldcountry':
            ramp = goldcountry[colorRamp];
            break;
        case 'hubnerseed':
            ramp = hubnerseed[colorRamp];
            break;
        case 'hcp':
            ramp = hcp[colorRamp];
            break;
        case 'intacta':
            ramp = intacta[colorRamp];
            break;
        case 'jungseedgenetics':
            ramp = jungseedgenetics[colorRamp];
            break;
        case 'kruger':
            ramp = kruger[colorRamp];
            break;
        case 'latijereta':
            ramp = latijereta[colorRamp];
            break;
        case 'lewishybrids':
            ramp = lewishybrids[colorRamp];
            break;
        case 'monsoy':
            ramp = monsoy[colorRamp];
            break;
        case 'rea':
            ramp = rea[colorRamp];
            break;
        case 'specialtyhybrids':
            ramp = specialtyhybrids[colorRamp];
            break;
        case 'stewart':
            ramp = stewart[colorRamp];
            break;
        case 'stoneseed':
            ramp = stoneseed[colorRamp];
            break;
        case 'verquvo':
            ramp = verquvo[colorRamp];
            break;
        case 'vegstate':
            ramp = vegstate[colorRamp];
            break;
        case 'westbred':
            ramp = westbred[colorRamp];
            break;
        case 'utility':
            ramp = utility;
            break;
        default:
            if (element[colorRamp]) {
                ramp = element[colorRamp];
            } else {
                edsWarning(
                    `The ${theme} theme does not have the ${colorRamp} color ramp.`
                );
            }
    }
    return ramp;
};

export default getColorRamp;
