const express = require('express')
const Router = express.Router

generateHtml = (options) => {
    const {baseUrl, name} = options
    return `
    <!DOCTYPE html>
        <html>
        <head>
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <meta http-equiv="X-UA-Compatible" content="IE=edge">
            <title>${name} - GraphQL Docs</title>
            <link rel="stylesheet" href="${baseUrl}/style.css">
            <script>window.graphqlDocsOptions=${JSON.stringify(options)}</script>
        </head>
        <body>
            <div class="contents"></div>
            <script>
                window.phoenix = {}
            </script>
            <script type="text/javascript" src="${baseUrl}/bundle.js"></script>
        </body>
    </html>
    `
}

module.exports = (options) => {
    const router = Router()

    router.use(`/style.css`, express.static(`${__dirname}/public/styles/style.css`))
    router.use(`/bundle.js`, express.static(`${__dirname}/public/scripts/bundle.js`))
    router.use(`/material.js`, express.static(`${__dirname}/public/scripts/material.js`))
    router.use(`/voyager.worker.js`, express.static(`${__dirname}/public/scripts/voyager.worker.js`))

    router.get('/*', (req, res) => {
        res.send(generateHtml(options))
    })

    return router
}
