/* eslint-disable react/function-component-definition,react/destructuring-assignment */
module.exports = (cookie, cookies) => {
    const tokenArray = []
    if (cookies.length < 1) return null
    if (cookie) {
        cookie.split(';').map((individualCookie) =>
            cookies.map((cookieName) => {
                if (individualCookie.includes(cookieName)) {
                    const token = individualCookie.split('=')
                    tokenArray.push(`Bearer ${token[1].trim()}`)
                }
                return false
            })
        )
    } else {
        return null
    }
    return tokenArray[0]
}
