const {expect} = require('chai')
const getBearerToken = require('../src/getBearerToken')

describe('getBearerToken', () => {
    const cookie = 'foo-token= loremipsumtoken;token-foo=tokenloremipsum'
    const cookies = ['foo-token', 'token-foo']
    it('should return a bearerToken when valid values are passed', () => {
        const token = getBearerToken(cookie, cookies)
        token.should.eql('Bearer loremipsumtoken')
    })
    it('should return null when no cookie names are passed', () => {
        const token = getBearerToken(cookie, [])
        expect(token).to.eql(null)
    })
    it('should return null when no cookie is passed', () => {
        const token = getBearerToken('', cookies)
        expect(token).to.eql(null)
    })
})
