import React from 'react'
import {render} from '@testing-library/react'
import Banner from '../../../menu-ui/scripts/banners/Banner'

describe('Banner', () => {
    it('sets classes based on visibility.', () => {
        const {rerender} = render(<Banner isVisible />)
        document.querySelector('.phoenix-banner-entering').should.exists
        rerender(<Banner isVisible={false} />)
        document.querySelector('.phoenix-banner-exiting').should.exists
    })

    it('when hidden, sets top to the inverse of the banner height plus the navbar height', () => {
        render(<Banner isVisible={false} />)
        document.querySelectorAll('.phoenix-banner-entering').length.should.eql(0)
        document.querySelector('.phoenix-banner-exiting').should.exists
    })

    it('when visible, sets top to the navbar height', () => {
        render(<Banner isVisible />)
        document.querySelector('.phoenix-banner-entering').should.exists
        document.querySelectorAll('.phoenix-banner-exiting').length.should.eql(0)
    })
})
