const subDomains = ['preferences', 'messaging', 'navigation', 'profile', 'user-feedback']

module.exports = function(cloudFoundryBindings) {
    const home = cloudFoundryBindings['phoenix-home'] || 'phoenix-tools-np.io'
    const defaults = {
        'phoenix-home': home,
        'home-url': `https://${home}/home`
    }
    subDomains.forEach((subDomain) => {
        defaults[`${subDomain}-url`] = `https://${subDomain}.${home}`
    })
    return defaults
}
